client/src/pages/admin-registrations-pag.content:
import { useParams } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { CheckCircle2, Clock, Trophy, QrCode, Check } from "lucide-react";
import type { Tournament } from "@shared/schema";

interface PendingRegistration {
  id: string;
  tournamentId: string;
  playerId: string;
  registrationTime: string;
  enteringHighHands: boolean;
  paymentConfirmed: boolean;
  player: {
    id: string;
    name: string;
    phone?: string;
  } | null;
  amountOwed: number;
}

interface ConfirmedRegistration {
  id: string;
  tournamentId: string;
  playerId: string;
  registrationTime: string;
  enteringHighHands: boolean;
  paymentConfirmed: boolean;
  player: {
    id: string;
    name: string;
    phone?: string;
  } | null;
  amountPaid: number;
}

export function AdminRegistrationsPage() {
  const { tournamentId } = useParams<{ tournamentId: string }>();
  const { toast } = useToast();

  const { data: tournament, isLoading: tournamentLoading } = useQuery<Tournament>({
    queryKey: ["/api/tournaments", tournamentId],
  });

  const { data: pendingRegistrations = [], refetch } = useQuery<PendingRegistration[]>({
    queryKey: [`/api/tournaments/${tournamentId}/pending-registrations`],
    refetchInterval: 3000,
  });

  const { data: confirmedRegistrations = [], refetch: refetchConfirmed } = useQuery<ConfirmedRegistration[]>({
    queryKey: [`/api/tournaments/${tournamentId}/confirmed-registrations`],
    refetchInterval: 3000,
  });

  const confirmPaymentMutation = useMutation({
    mutationFn: async (registrationId: string) => {
      const response = await apiRequest("PATCH", `/api/registrations/${registrationId}/confirm-payment`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/tournaments/${tournamentId}/pending-registrations`] });
      queryClient.invalidateQueries({ queryKey: [`/api/tournaments/${tournamentId}/confirmed-registrations`] });
      toast({
        title: "Payment Confirmed",
        description: "Registration payment has been confirmed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to confirm payment. Please try again.",
        variant: "destructive",
      });
    },
  });

  useEffect(() => {
    const interval = setInterval(() => {
      refetch();
      refetchConfirmed();
    }, 3000);

    return () => clearInterval(interval);
  }, [refetch, refetchConfirmed]);

  if (tournamentLoading) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardContent className="pt-6">
            <p className="text-center text-muted-foreground">Loading tournament...</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!tournament) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardContent className="pt-6">
            <p className="text-center text-destructive">Tournament not found</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  const registrationUrl = `${window.location.origin}/register/${tournamentId}`;

  return (
    <div className="container mx-auto p-6 space-y-6">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>{tournament.name}</CardTitle>
              <CardDescription>Pending Payment Confirmations</CardDescription>
            </div>
            <Button
              onClick={() => window.open(`/admin/qr/${tournamentId}`, '_blank')}
              variant="outline"
              data-testid="view-qr-code"
            >
              <QrCode className="w-4 h-4 mr-2" />
              Show QR Code
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="mb-4 p-4 bg-muted rounded-lg">
            <p className="text-sm font-medium mb-2">Registration Link</p>
            <div className="flex items-center space-x-2">
              <code className="flex-1 p-2 bg-background rounded text-xs overflow-x-auto">
                {registrationUrl}
              </code>
              <Button
                size="sm"
                variant="outline"
                onClick={() => {
                  navigator.clipboard.writeText(registrationUrl);
                  toast({
                    title: "Copied!",
                    description: "Registration link copied to clipboard.",
                  });
                }}
                data-testid="copy-registration-link"
              >
                Copy
              </Button>
            </div>
          </div>

          <div className="rounded-md border">
            {pendingRegistrations.length === 0 ? (
              <div className="p-8 text-center text-muted-foreground">
                <Clock className="w-12 h-12 mx-auto mb-3 opacity-50" />
                <p className="font-medium">No pending registrations</p>
                <p className="text-sm">New registrations will appear here automatically</p>
              </div>
            ) : (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Player</TableHead>
                    <TableHead>Phone</TableHead>
                    <TableHead>High Hands</TableHead>
                    <TableHead>Amount</TableHead>
                    <TableHead>Time</TableHead>
                    <TableHead className="text-right">Action</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {pendingRegistrations.map((registration) => (
                    <TableRow key={registration.id} data-testid={`registration-${registration.id}`}>
                      <TableCell className="font-medium">
                        {registration.player?.name || "Unknown Player"}
                      </TableCell>
                      <TableCell className="text-muted-foreground">
                        {registration.player?.phone || "N/A"}
                      </TableCell>
                      <TableCell>
                        {registration.enteringHighHands ? (
                          <Badge variant="secondary">
                            <Trophy className="w-3 h-3 mr-1" />
                            Yes
                          </Badge>
                        ) : (
                          <Badge variant="outline">No</Badge>
                        )}
                      </TableCell>
                      <TableCell className="font-semibold text-lg">
                        ${registration.amountOwed.toFixed(2)}
                      </TableCell>
                      <TableCell className="text-muted-foreground text-sm">
                        {new Date(registration.registrationTime).toLocaleTimeString()}
                      </TableCell>
                      <TableCell className="text-right">
                        <Button
                          onClick={() => confirmPaymentMutation.mutate(registration.id)}
                          disabled={confirmPaymentMutation.isPending}
                          size="sm"
                          data-testid={`confirm-payment-${registration.id}`}
                        >
                          <CheckCircle2 className="w-4 h-4 mr-2" />
                          Confirm Payment
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            )}
          </div>

          {pendingRegistrations.length > 0 && (
            <div className="mt-4 p-4 bg-muted rounded-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium">Total Pending</p>
                  <p className="text-2xl font-bold">
                    ${pendingRegistrations.reduce((sum, reg) => sum + reg.amountOwed, 0).toFixed(2)}
                  </p>
                </div>
                <div className="text-right">
                  <p className="text-sm text-muted-foreground">{pendingRegistrations.length} player(s)</p>
                  <p className="text-xs text-muted-foreground">awaiting confirmation</p>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Confirmed Players</CardTitle>
          <CardDescription>Players who have paid and are registered</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="rounded-md border">
            {confirmedRegistrations.length === 0 ? (
              <div className="p-8 text-center text-muted-foreground">
                <CheckCircle2 className="w-12 h-12 mx-auto mb-3 opacity-50" />
                <p className="font-medium">No confirmed players yet</p>
                <p className="text-sm">Confirmed players will appear here</p>
              </div>
            ) : (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Player</TableHead>
                    <TableHead>Phone</TableHead>
                    <TableHead>High Hands</TableHead>
                    <TableHead>Amount Paid</TableHead>
                    <TableHead>Registered At</TableHead>
                    <TableHead className="text-right">Status</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {confirmedRegistrations.map((registration) => (
                    <TableRow key={registration.id} data-testid={`confirmed-registration-${registration.id}`}>
                      <TableCell className="font-medium">
                        {registration.player?.name || "Unknown Player"}
                      </TableCell>
                      <TableCell className="text-muted-foreground">
                        {registration.player?.phone || "N/A"}
                      </TableCell>
                      <TableCell>
                        {registration.enteringHighHands ? (
                          <Badge variant="secondary">
                            <Trophy className="w-3 h-3 mr-1" />
                            Yes
                          </Badge>
                        ) : (
                          <Badge variant="outline">No</Badge>
                        )}
                      </TableCell>
                      <TableCell className="font-semibold text-lg">
                        ${registration.amountPaid.toFixed(2)}
                      </TableCell>
                      <TableCell className="text-muted-foreground text-sm">
                        {new Date(registration.registrationTime).toLocaleTimeString()}
                      </TableCell>
                      <TableCell className="text-right">
                        <Badge className="bg-green-100 text-green-800">
                          <Check className="w-3 h-3 mr-1" />
                          Confirmed
                        </Badge>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            )}
          </div>

          {confirmedRegistrations.length > 0 && (
            <div className="mt-4 p-4 bg-green-50 rounded-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-green-900">Total Collected</p>
                  <p className="text-2xl font-bold text-green-900">
                    ${confirmedRegistrations.reduce((sum, reg) => sum + reg.amountPaid, 0).toFixed(2)}
                  </p>
                </div>
                <div className="text-right">
                  <p className="text-sm text-green-700">{confirmedRegistrations.length} player(s)</p>
                  <p className="text-xs text-green-600">confirmed and paid</p>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle className="text-sm font-medium">Auto-Refresh</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
            <p className="text-sm text-muted-foreground">
              This page updates automatically every 3 seconds
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/public-register-page.ts.content:
import { useState } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { CheckCircle2, UserPlus, Users, Trophy } from "lucide-react";
import type { Tournament, Player } from "@shared/schema";

const newPlayerSchema = z.object({
  name: z.string().min(1, "Name is required"),
  phone: z.string().min(1, "Phone number is required"),
});

export function PublicRegisterPage() {
  const { tournamentId } = useParams<{ tournamentId: string }>();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [step, setStep] = useState(1);
  const [playerType, setPlayerType] = useState<"new" | "existing" | null>(null);
  const [selectedPlayer, setSelectedPlayer] = useState<string>("");
  const [enteringHighHands, setEnteringHighHands] = useState(false);
  const [registrationComplete, setRegistrationComplete] = useState(false);

  const form = useForm({
    resolver: zodResolver(newPlayerSchema),
    defaultValues: {
      name: "",
      phone: "",
    },
  });

  const { data: tournament, isLoading: tournamentLoading } = useQuery<Tournament>({
    queryKey: ["/api/tournaments", tournamentId],
  });

  const { data: players = [] } = useQuery<Player[]>({
    queryKey: ["/api/players"],
  });

  const createPlayerMutation = useMutation({
    mutationFn: async (data: { name: string; phone: string }) => {
      const response = await apiRequest("POST", "/api/players", data);
      const result = await response.json();

      if (response.status === 409) {
        throw new Error(result.message || "Phone number already registered");
      }

      if (!response.ok) {
        throw new Error(result.error || "Failed to create player");
      }

      return result;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/players"] });
    },
  });

  const registerMutation = useMutation({
    mutationFn: async (data: { playerId: string; tournamentId: string; enteringHighHands: boolean }) => {
      const response = await apiRequest("POST", `/api/tournaments/${data.tournamentId}/registrations`, {
        playerId: data.playerId,
        enteringHighHands: data.enteringHighHands,
      });
      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || "Failed to register for tournament");
      }

      return result;
    },
    onSuccess: () => {
      setRegistrationComplete(true);
      toast({
        title: "Registration Successful!",
        description: "Please proceed to payment confirmation.",
      });
    },
    onError: (error) => {
      const errorMessage = error instanceof Error ? error.message : "Please try again or contact support.";
      toast({
        title: "Registration Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleStep1Next = () => {
    if (!playerType) {
      toast({
        title: "Selection Required",
        description: "Please choose if you're a new or existing player.",
        variant: "destructive",
      });
      return;
    }
    setStep(2);
  };

  const handleStep2Next = async () => {
    if (playerType === "new") {
      const isValid = await form.trigger();
      if (!isValid) return;

      const formData = form.getValues();
      try {
        const newPlayer = await createPlayerMutation.mutateAsync(formData);
        setSelectedPlayer(newPlayer.id);
        setStep(3);
      } catch (error) {
        const errorMessage = error instanceof Error ? error.message : "Failed to create player. Please try again.";
        toast({
          title: "Unable to Create Player",
          description: errorMessage,
          variant: "destructive",
        });
      }
    } else {
      if (!selectedPlayer) {
        toast({
          title: "Selection Required",
          description: "Please select a player.",
          variant: "destructive",
        });
        return;
      }
      setStep(3);
    }
  };

  const handleFinalSubmit = () => {
    if (!selectedPlayer || !tournamentId) return;

    registerMutation.mutate({
      playerId: selectedPlayer,
      tournamentId,
      enteringHighHands,
    });
  };

  const handleStartOver = () => {
    setStep(1);
    setPlayerType(null);
    setSelectedPlayer("");
    setEnteringHighHands(false);
    setRegistrationComplete(false);
    form.reset();
  };

  if (tournamentLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <Card className="w-full max-w-md">
          <CardContent className="pt-6">
            <p className="text-center text-muted-foreground">Loading tournament...</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!tournament) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <Card className="w-full max-w-md">
          <CardContent className="pt-6">
            <p className="text-center text-destructive">Tournament not found</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (registrationComplete) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background p-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <CheckCircle2 className="w-16 h-16 text-green-500 mx-auto mb-4" />
            <CardTitle className="text-2xl">Registration Complete!</CardTitle>
            <CardDescription>
              You're registered for {tournament.name}
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="bg-muted p-4 rounded-lg space-y-2">
              <p className="text-sm font-medium">Please proceed to the payment desk</p>
              <p className="text-2xl font-bold">
                $
                {(
                  parseFloat(tournament.buyInAmount) +
                  (enteringHighHands && tournament.enableHighHand
                    ? parseFloat(tournament.highHandAmount || "0")
                    : 0)
                ).toFixed(2)}
              </p>
              <p className="text-xs text-muted-foreground">
                Buy-in: ${parseFloat(tournament.buyInAmount).toFixed(2)}
                {enteringHighHands && tournament.enableHighHand && (
                  <> + High Hands: ${parseFloat(tournament.highHandAmount || "0").toFixed(2)}</>
                )}
              </p>
            </div>
            <Button onClick={handleStartOver} variant="outline" className="w-full" data-testid="register-another">
              Register Another Player
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle>{tournament.name}</CardTitle>
          <CardDescription>Tournament Registration</CardDescription>
          <div className="flex items-center justify-between mt-4">
            {[1, 2, 3].map((s) => (
              <div
                key={s}
                className={`flex-1 h-2 rounded ${
                  s <= step ? "bg-primary" : "bg-muted"
                } ${s !== 3 ? "mr-2" : ""}`}
              />
            ))}
          </div>
          <p className="text-sm text-muted-foreground mt-2">
            Step {step} of 3
          </p>
        </CardHeader>
        <CardContent>
          {step === 1 && (
            <div className="space-y-4">
              <Label className="text-base font-semibold">Are you a new or existing player?</Label>
              <RadioGroup value={playerType || ""} onValueChange={(value) => setPlayerType(value as "new" | "existing")}>
                <div
                  className={`flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-colors ${
                    playerType === "new" ? "border-primary bg-primary/5" : "border-border hover:border-primary/50"
                  }`}
                  onClick={() => setPlayerType("new")}
                >
                  <RadioGroupItem value="new" id="new" data-testid="player-type-new" />
                  <Label htmlFor="new" className="flex items-center cursor-pointer flex-1">
                    <UserPlus className="w-5 h-5 mr-2" />
                    <span className="font-medium">New Player</span>
                  </Label>
                </div>
                <div
                  className={`flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-colors ${
                    playerType === "existing" ? "border-primary bg-primary/5" : "border-border hover:border-primary/50"
                  }`}
                  onClick={() => setPlayerType("existing")}
                >
                  <RadioGroupItem value="existing" id="existing" data-testid="player-type-existing" />
                  <Label htmlFor="existing" className="flex items-center cursor-pointer flex-1">
                    <Users className="w-5 h-5 mr-2" />
                    <span className="font-medium">Existing Player</span>
                  </Label>
                </div>
              </RadioGroup>
              <Button onClick={handleStep1Next} className="w-full" data-testid="step1-next">
                Continue
              </Button>
            </div>
          )}

          {step === 2 && (
            <div className="space-y-4">
              {playerType === "new" ? (
                <Form {...form}>
                  <form className="space-y-4">
                    <FormField
                      control={form.control}
                      name="name"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Full Name</FormLabel>
                          <FormControl>
                            <Input placeholder="John Doe" {...field} data-testid="player-name" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="phone"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Phone Number</FormLabel>
                          <FormControl>
                            <Input placeholder="(555) 123-4567" {...field} data-testid="player-phone" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </form>
                </Form>
              ) : (
                <div className="space-y-2">
                  <Label>Select Your Name</Label>
                  <Select value={selectedPlayer} onValueChange={setSelectedPlayer}>
                    <SelectTrigger data-testid="player-select">
                      <SelectValue placeholder="Choose your name" />
                    </SelectTrigger>
                    <SelectContent>
                      {players.map((player) => (
                        <SelectItem key={player.id} value={player.id}>
                          {player.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              )}
              <div className="flex space-x-2">
                <Button onClick={() => setStep(1)} variant="outline" className="flex-1" data-testid="step2-back">
                  Back
                </Button>
                <Button
                  onClick={handleStep2Next}
                  className="flex-1"
                  disabled={createPlayerMutation.isPending}
                  data-testid="step2-next"
                >
                  {createPlayerMutation.isPending ? "Creating..." : "Continue"}
                </Button>
              </div>
            </div>
          )}

          {step === 3 && (
            <div className="space-y-4">
              {tournament.enableHighHand && (
                <div className="space-y-4">
                  <Label className="text-base font-semibold">High Hands Entry</Label>
                  <p className="text-sm text-muted-foreground">
                    Enter the high hands competition for an additional ${parseFloat(tournament.highHandAmount || "0").toFixed(2)}
                  </p>
                  <RadioGroup
                    value={enteringHighHands ? "yes" : "no"}
                    onValueChange={(value) => setEnteringHighHands(value === "yes")}
                  >
                    <div
                      className={`flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-colors ${
                        enteringHighHands ? "border-primary bg-primary/5" : "border-border hover:border-primary/50"
                      }`}
                      onClick={() => setEnteringHighHands(true)}
                    >
                      <RadioGroupItem value="yes" id="high-hands-yes" data-testid="high-hands-yes" />
                      <Label htmlFor="high-hands-yes" className="flex items-center cursor-pointer flex-1">
                        <Trophy className="w-5 h-5 mr-2" />
                        <span className="font-medium">Yes, I'm in!</span>
                      </Label>
                    </div>
                    <div
                      className={`flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-colors ${
                        !enteringHighHands ? "border-primary bg-primary/5" : "border-border hover:border-primary/50"
                      }`}
                      onClick={() => setEnteringHighHands(false)}
                    >
                      <RadioGroupItem value="no" id="high-hands-no" data-testid="high-hands-no" />
                      <Label htmlFor="high-hands-no" className="flex items-center cursor-pointer flex-1">
                        <span className="font-medium">No thanks</span>
                      </Label>
                    </div>
                  </RadioGroup>
                </div>
              )}

              {!tournament.enableHighHand && (
                <div className="bg-muted p-4 rounded-lg">
                  <p className="text-sm text-muted-foreground text-center">
                    High hands competition is not available for this tournament
                  </p>
                </div>
              )}

              <div className="bg-muted p-4 rounded-lg">
                <p className="text-sm font-medium mb-2">Total Amount</p>
                <p className="text-3xl font-bold">
                  $
                  {(
                    parseFloat(tournament.buyInAmount) +
                    (enteringHighHands && tournament.enableHighHand
                      ? parseFloat(tournament.highHandAmount || "0")
                      : 0)
                  ).toFixed(2)}
                </p>
              </div>

              <div className="flex space-x-2">
                <Button onClick={() => setStep(2)} variant="outline" className="flex-1" data-testid="step3-back">
                  Back
                </Button>
                <Button
                  onClick={handleFinalSubmit}
                  className="flex-1"
                  disabled={registerMutation.isPending}
                  data-testid="complete-registration"
                >
                  {registerMutation.isPending ? "Registering..." : "Complete Registration"}
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/qr-code-page.tsx.content:
import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { QRCodeSVG } from "qrcode.react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import type { Tournament } from "@shared/schema";

export function QRCodePage() {
  const { tournamentId } = useParams<{ tournamentId: string }>();

  const { data: tournament, isLoading } = useQuery<Tournament>({
    queryKey: ["/api/tournaments", tournamentId],
  });

  const registrationUrl = `${window.location.origin}/register/${tournamentId}`;

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <Card className="w-full max-w-md">
          <CardContent className="pt-6">
            <p className="text-center text-muted-foreground">Loading...</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!tournament) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <Card className="w-full max-w-md">
          <CardContent className="pt-6">
            <p className="text-center text-destructive">Tournament not found</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-2xl">
        <CardHeader className="text-center">
          <CardTitle className="text-3xl">{tournament.name}</CardTitle>
          <CardDescription className="text-lg">Self-Registration QR Code</CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="flex justify-center p-8 bg-white rounded-lg">
            <QRCodeSVG
              value={registrationUrl}
              size={400}
              level="H"
              includeMargin={true}
            />
          </div>

          <div className="text-center space-y-2">
            <p className="text-lg font-semibold">Scan to Register</p>
            <p className="text-sm text-muted-foreground">
              Players can scan this QR code with their phone camera to register for the tournament
            </p>
          </div>

          <div className="bg-muted p-4 rounded-lg">
            <p className="text-xs font-medium mb-2">Registration URL</p>
            <code className="text-xs break-all">{registrationUrl}</code>
          </div>

          <div className="grid grid-cols-2 gap-4 pt-4 border-t">
            <div className="text-center">
              <p className="text-sm text-muted-foreground">Buy-in</p>
              <p className="text-2xl font-bold">${parseFloat(tournament.buyInAmount).toFixed(2)}</p>
            </div>
            {tournament.enableHighHand && (
              <div className="text-center">
                <p className="text-sm text-muted-foreground">High Hands</p>
                <p className="text-2xl font-bold">
                  ${parseFloat(tournament.highHandAmount || "0").toFixed(2)}
                </p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ui/form-slideout.t.content:
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
  SheetFooter,
} from "@/components/ui/sheet";
import { cn } from "@/lib/utils";

interface FormSlideoutProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  title: string;
  description?: string;
  children: React.ReactNode;
  footer?: React.ReactNode;
  side?: "right" | "left" | "top" | "bottom";
  className?: string;
}

export function FormSlideout({
  open,
  onOpenChange,
  title,
  description,
  children,
  footer,
  side = "right",
  className,
}: FormSlideoutProps) {
  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent 
        side={side} 
        className={cn(
          "flex flex-col w-full sm:max-w-[540px] md:max-w-[640px] lg:max-w-[720px]",
          className
        )}
      >
        <SheetHeader className="shrink-0">
          <SheetTitle>{title}</SheetTitle>
          {description && <SheetDescription>{description}</SheetDescription>}
        </SheetHeader>

        <div className="flex-1 overflow-y-auto py-4 px-1">
          {children}
        </div>

        {footer && (
          <SheetFooter className="shrink-0 border-t pt-4">
            {footer}
          </SheetFooter>
        )}
      </SheetContent>
    </Sheet>
  );
}
